import React from 'react';
import {Container, ModalBody} from './styled';

export default ({ children, status, setStatus }) => {

    const handleModalClick  =  () =>{
        setStatus(false);
    }

    return(
        <>
            {status == true &&
                <Container onClick={handleModalClick}>

                    <ModalBody>
                            {children}
                    </ModalBody>

                </Container>
            }
        </>

    );
}
