import React from 'react';
import {
    Container, 
    ProductPhotoArea, 
    ProductInfoArea, 
    ProductButtonArea,
    ProductPhoto,
    ProductName,
    ProductPrice,
    ProductIngredients,
    ProductButton
} from './styled';


export default ({data, onClick}) => {
    const handleClick = () =>{
        onClick();
    }

    return(
        <Container onClick={handleClick}>

            <ProductPhotoArea >
                <ProductPhoto src={data.image} />
            </ProductPhotoArea> 

            <ProductInfoArea>
                <ProductName>{data.name}</ProductName>

                <ProductPrice>R$ {data.price}</ProductPrice>
                <ProductIngredients>{data.ingredients}</ProductIngredients>
            </ProductInfoArea>

            <ProductButtonArea>
                <ProductButton src='/assets/next.png'/>
            </ProductButtonArea>

        </Container>
    );
}
