import React, { useState } from 'react';
import {
    Container,
    ProductArea,
    ProdutcPhoto,
    ProductInfoArea,
    ProductDetails,
    ProductQuantityArea,
    ProductButtons,
    ProductName,
    ProductIngredients,
    ProductButton,
    ProductQuantity,
    ProductQtImage,
    ProductQtText,
    ProductPrice
} from './styled';

export default ({data, setStatus}) => {
    const [minus, setMinus] = useState(1);

    const handleMinusQt = () => {
        let qtdTotal = minus + 1; 
        setMinus(minus + 1);
    }


    const handleCancelButton = () => {
        setStatus(false);
    }

    return(
        <Container>

            <ProductArea>

                <ProdutcPhoto src={data.image} />

                <ProductInfoArea>
                    <ProductDetails>
                        <ProductName>{data.name}</ProductName>
                        <ProductIngredients>{data.ingredients}</ProductIngredients>
                    </ProductDetails>

                    <ProductQuantityArea>
                        <ProductQuantity>
                            <ProductQtImage  src="/assets/minus.png" />
                                <ProductQtText>{minus}</ProductQtText>
                            <ProductQtImage onClick={handleMinusQt} src="/assets/plus.png" />
                        </ProductQuantity>
                        <ProductPrice>
                            R$ {data.price}
                        </ProductPrice>
                    </ProductQuantityArea>
                </ProductInfoArea>

            </ProductArea>

            <ProductButtons>
                <ProductButton 
                    small={true} 
                    onClick={handleCancelButton}
                >
                Cancelar</ProductButton>
                <ProductButton>Adicionar ao Carrinho</ProductButton>
            </ProductButtons>

        </Container>
    );
}
